s AS notpub \r\nLEFT JOIN (\r\n  SELECT notmul.noticiacod, notmul.multimediacod  \r\n  FROM not_noticias_mul_multimedia AS notmul \r\n  INNER JOIN \r\n  (\r\n   SELECT noticiacod, MIN(notmultimediaorden) AS notmultimediaorden \r\n   FROM not_noticias_mul_multimedia  \r\n   GROUP BY noticiacod\r\n  ) menor \r\n  ON notmul.noticiacod=menor.noticiacod AND notmul.notmultimediaorden= menor.notmultimediaorden \r\n  GROUP BY noticiacod\r\n) \r\nnoticiamultimedia \r\nON notpub.noticiacod=noticiamultimedia.noticiacod \r\nLEFT JOIN mul_multimedia AS c ON noticiamultimedia.multimediacod=c.multimediacod\r\nLEFT JOIN mul_multimedia_tipos mt ON mt.`multimediatipocod`=c.`multimediatipocod` AND mt.`multimediaconjuntocod`=1\r\nLEFT JOIN mul_multimedia_tipos mt2 ON mt2.`multimediatipocod`=c.`multimediatipocod` AND mt2.`multimediaconjuntocod`=2\r\nLEFT JOIN mul_multimedia_categorias AS mc ON c.multimediacatcod=mc.multimediacatcod\r\nWHERE notpub.catcod=\"#pcatcod#\" \r\nAND notpub.noticiacod NOT IN (#pnoticiacod#)\r\nORDER BY #porderby# #plimit#',NULL,0,NULL),(2478,'sel_tap_tapas_zonas_xmodulodata_xtapacod','SEL','TAP_TAPAS_ZONAS_MODULOS','SELECT * FROM tap_tapas_zonas_modulos WHERE tapacod=\"#ptapacod#\" AND modulodata LIKE \"%#pmodulodata#%\"',NULL,0,NULL),(2479,'upd_pag_paginas_mul_multimedia_multimediadesc_xpagcod_multimediacod','UPD','PAG_PAGINAS_MUL_MULTIMEDIA','UPDATE pag_paginas_mul_multimedia SET pagmultimediadesc=\"#ppagmultimediadesc#\", ultmodusuario=\"#pultmodusuario#\", ultmodfecha=\"#pultmodfecha#\" WHERE pagcod=\"#ppagcod#\" AND multimediacod=\"#pmultimediacod#\"','',1,'2014-01-13 15:10:59'),(2480,'upd_pag_paginas_mul_multimedia_titulo_xpagcod_multimediacod','UPD','PAG_PAGINAS_MUL_MULTIMEDIA','UPDATE pag_paginas_mul_multimedia SET pagmultimediatitulo=\"#ppagmultimediatitulo#\", ultmodusuario=\"#pultmodusuario#\", ultmodfecha=\"#pultmodfecha#\" WHERE pagcod=\"#ppagcod#\" AND  multimediacod=\"#pmultimediacod#\"','',1,'2014-01-13 15:10:10'),(2481,'upd_not_noticias_mul_mul