ultimediatitulo END AS multimediatitulo, \r\nCASE WHEN gm2.galmultimediadesc IS NULL THEN m.multimediadesc ELSE  gm2.galmultimediadesc END AS multimediadesc, \r\nmt.multimediatipoarchivo, mmc.`multimediaconjuntodesc` , gm.galmultimediaorden AS multimediaorden, cant.cantidad\r\nFROM gal_albums_gal_galerias AS ag\r\nINNER JOIN gal_albums AS a ON ag.albumcod= a.albumcod\r\nINNER JOIN gal_galerias AS g ON ag.galeriacod = g.galeriacod\r\nLEFT JOIN\r\n(\r\n	SELECT * FROM(	\r\n	SELECT gm.galmultimediaorden,  gm.multimediacod,gm. galeriacod, gm.galmultimediatitulo\r\n	FROM gal_galerias_mul_multimedia gm\r\n	ORDER BY gm.galmultimediaorden ASC) AS a \r\n	GROUP BY a.galeriacod\r\n) AS gm\r\nON gm.galeriacod=g.galeriacod\r\nLEFT JOIN\r\n(\r\n	SELECT galeriacod ,COUNT(*) AS cantidad FROM(	\r\n	SELECT galeriacod\r\n	FROM gal_galerias_mul_multimedia) AS a \r\n	GROUP BY a.galeriacod\r\n) AS cant\r\nON cant.galeriacod = g.galeriacod\r\nLEFT JOIN mul_multimedia m\r\nON m.multimediacod=gm.multimediacod \r\nLEFT JOIN mul_multimedia_tipos mt\r\nON mt.multimediatipocod=m.multimediatipocod AND mt.multimediaconjuntocod=1 /*solo fotos*/\r\nLEFT JOIN `gal_galerias_mul_multimedia` gm2\r\nON gm2.multimediacod=gm.multimediacod AND gm2.galeriacod=g.`galeriacod`\r\nLEFT JOIN mul_multimedia_categorias mc\r\nON mc.multimediacatcod=m.multimediacatcod \r\nLEFT JOIN `mul_multimedia_conjuntos` AS mmc ON mmc.`multimediaconjuntocod`=g.`multimediaconjuntocod`\r\nWHERE \r\nIF (\"#pxalbumcod#\",ag.albumcod = \"#palbumcod#\",1)\r\nAND\r\nIF (\"#pxalbumtitulo#\",a.albumtitulo LIKE \"%#palbumtitulo#%\",1)\r\nAND\r\nIF (\"#pxgaleriatitulo#\",g.galeriatitulo LIKE \"%#pgaleriatitulo#%\",1)\r\nAND\r\nIF (\"#pxgaleriaestadocod#\", g.galeriaestadocod IN (#pgaleriaestadocod#),1)\r\nORDER BY #porderby# #plimit#','',1,'2012-07-02 16:07:26'),(2576,'ins_gal_galerias','INS','GAL_GALERIAS','INSERT INTO gal_galerias (\r\n    multimediaconjuntocod,\r\n    catcod, \r\n    albumcod,\r\n    galeriatitulo,\r\n    galeriadesc,\r\