INSERT INTO `stored_procedures_front` VALUES (1,'sel_stored','SEL','STORED_PROCEDURES','select * from stored_procedures \r\nwhere \r\nif (\"#pestadocod#\",spcod=\"#pspcod#\",1) \r\nand \r\nif (\"#pestadonombre#\",spnombre=\"#pspnombre#\",1) \r\nand \r\nif (\"#pestadoopert#\",spoperacion=\"#pspoperacion#\",1) \r\nand \r\nif (\"#pestadotabla#\",sptabla=\"#psptabla#\",1) \r\nand \r\nif (\"#pestadosql#\",spsqlstring=\"#pspsqlstring#\",1) \r\nand \r\nif (\"#pestadoobs#\",spobserv=\"#pspobserv#\",1) \r\norder by #porderby#','',0,'0000-00-00 00:00:00'),(2,'sel_not_noticias_publicadas_xcodigo','SEL','NOT_NOTICIAS_PUBLICADAS','SELECT * FROM not_noticias_publicadas AS nnp \r\nWHERE nnp.noticiacod=\"#pnoticiacod#\"','',1,'2016-01-22 19:31:11'),(21,'sel_not_noticias_mul_multimedia_xnoticiacod','SEL','NOT_NOTICIAS_MUL_MULTIMEDIA','SELECT a.multimediacod, a.notmultimediaorden, b.multimediaidexterno, b.multimediatitulo, b.multimediacatcod, b.multimediatipocod, \r\nb.multimediaubic, b.multimedianombre, a.notmultimediatitulo AS multimediatitulo, a.notmultimediadesc AS multimediadesc, b.multimediaestadocod, c.multimediacatcarpeta  \r\nFROM not_noticias_mul_multimedia AS a \r\nINNER JOIN mul_multimedia AS b ON a.multimediacod=b.multimediacod \r\nINNER JOIN mul_multimedia_categorias AS c ON b.multimediacatcod=c.multimediacatcod \r\nWHERE a.noticiacod=\"#pnoticiacod#\" AND a.multimediaconjuntocod = \"#pmultimediaconjuntocod#\" AND a.notmultimediamuestrahome=0 \r\nORDER BY a.notmultimediaorden \r\n#plimit#',NULL,0,NULL),(1718,'sel_mul_multimedia_constantes','SEL','MUL_MULTIMEDIA','SELECT multimediaestadocod AS valor, multimediaestadocte AS cte \r\nFROM mul_multimedia_estados \r\nUNION \r\nSELECT multimediaconjuntocod AS valor, multimediaconjuntocte AS cte\r\nFROM mul_multimedia_conjuntos\r\nUNION \r\nSELECT multimediatipocod AS valor, multimediatipoarchivo AS cte \r\nFROM mul_multimedia_tipos\r\nUNION \r\nSELECT multimediacatcarpeta AS valor, multimediacatcte AS cte \r\nFROM mul_multimed